#!/usr/bin/env python3\n\"\"\"\nExample of using the trained model in your own Python code.\n\"\"\"\n\nfrom predict import MentalHealthPredictor\n\ndef use_model_in_code():\n    \"\"\"Example of how to integrate the model into your application.\"\"\"\n    \n    # Initialize and load the model\n    predictor = MentalHealthPredictor()\n    predictor.load_model()\n    \n    # Example: Process a list of patient notes\n    patient_notes = [\n        \"Patient reports feeling sad and hopeless for the past two weeks\",\n        \"Experiencing panic attacks and excessive worry about work\", \n        \"Has been having thoughts of self-harm lately\"\n    ]\n    \n    # Get predictions for all notes\n    results = predictor.predict_batch(patient_notes)\n    \n    # Process results\n    for result in results:\n        print(f\"Note: {result['text'][:50]}...\")\n        print(f\"Classification: {result['prediction']}\")\n        print(f\"Confidence: {max(result['probabilities'].values()):.3f}\")\n        print(\"-\" * 40)\n    \n    # Example: Set up alerts for high-risk cases\n    high_risk_threshold = 0.7\n    \n    for result in results:\n        suicide_prob = result['probabilities']['Suicide']\n        if suicide_prob > high_risk_threshold:\n            print(f\"ðŸš¨ HIGH RISK ALERT: Suicide probability {suicide_prob:.3f}\")\n            print(f\"Text: {result['text']}\")\n            # Here you could trigger alerts, notifications, etc.\n\nif __name__ == \"__main__\":\n    use_model_in_code()\n